package kotlin_codefun main() {    var str: String?    loop@ while (true) {        print("Введи число: ")        str = readLine()        when {            str == "" -> println("Вы ничего не ввели")            str!!.all { it -> it.isDigit() || it == '-' } -> {                if (checkWriting(str))                {                    if(str!![0] == '-') {                        str = str.removeRange(0..0)                    }                    print("Вы ввели ${str.length}-ое число")                    break@loop                }                else                    println("Это не число")            }            else -> println("Это не число")        }    }}private fun checkWriting(str:String):Boolean {    str.forEachIndexed { index, it -> if (it == '-' && index != 0) return false }    return true}