package kotlin_codefun main() {    val num: Int    while (true) {        print("Введи число: ")        var line: String? = readLine()        if (line != "") {            if (line!!.all { it.isDigit() }) {                print("Вы ввели ${line.length}-ое число")                break            }            println("Это не число")            continue        } else {            println("Вы ничего не ввели")        }    }//    print("Введи число: ")//    val num = readLine()!!.toInt()//    print("Вы ввели $num")    }